var Router = Ember.Router.extend({
  location: config.locationType
});

Router.map(function() {
  this.resource('friends', function() {
    this.route('new');

    this.route('show', {
      path: ':friend_id'
    }, function() {
      this.resource('articles', function() {
        this.route('new');
      });

      this.route('what');
      this.route('fooo');
      this.route('adsfasdf');
      this.route('foooo');
    });

    this.route('edit', {
      path: ':friend_id/edit'
    });

    this.route("bar", function() {
      this.route("bar", function() {
        this.route("foaodfasdfasdf");
      });
    });
  });
});

export default Router;
